Exemplos:

                     // 1° Retorna o próximo valor para a propriedade informada.
                     int valor = WebServiceGenerico<DTO>
                                     .Proximo(p => p.Codigo);

                     // 2° Retorna o máximo valor para a propriedade informada.
                     string valor = WebServiceGenerico<DTO>
                                         .Maximo<string>(m => m.Nome);

                     // 3° Retorna o próximo valor para a propriedade conforme a condição informada.
                     int valor = WebServiceGenerico<DTO>
                                     .Condicao<int>(c => c.Tipo,"A")  // Obs: Sem o parâmetro operador a condição é igual.
                                     .Proximo(p => p.CodigoInterno);
                     Obs: Mesma regra para o máximo;

                     // 4° Retorna o próximo valor para a propriedade conforme as condições informadas.
                     int valor = WebServiceGenerico<DTO>
                                     .Condicao<int>(c => c.Tipo,"A")  // Obs: Sem o parâmetro operador a condição é igual.
                                     .Condicoes<int>(c => c.Status, "F", eOperador.Diferente)
                                     .Proximo(p => p.CodigoInterno);

                     Obs: Mesma regra para o máximo;

                     // 5° Retornar lista do objeto conforme a condição informada.
                     IEnumerable<ObjetoDTO> objetoDTO = WebServiceGenerico<ObjetoDTO>
                            .Condicao(c => c.Codigo, 1)
                            .Consultar()

                     // 6° Retornar tudo do objeto informada.
                     IEnumerable<ObjetoDTO> objetoDTO = WebServiceGenerico<ObjetoDTO>
                            .Consultar()
